import { useState, useEffect } from 'react';
import Cell from './Cell';

function getRandomValue() {
  let max = 36;
  let min = 1;
  return Math.floor(Math.random() * (max - min) + min);
}

const Gameboard = () => {

  const rows = 6;
  const cols = 6;
  const driver = [];

  useEffect(() => {
    for(let i = 0; i < rows; i++){
      driver[i] = [];
      for(let j = 0; j < cols; j++){
        driver[i][j] = {isClicked: false, value: getRandomValue()};
      }
    }
  }, []);

  function getDriver(){
    return driver;
  }

  //updating values whenever user clicks on a cell
  const handleClick = (row, col) => {
    const temp = getDriver();
    temp[row][col].isClicked = !temp[row][col].isClicked;
    driver = temp;
  };

  return (
    <div className="gameboard">
      <table>
        <tr>
          <Cell value="0" onClick={() => handleClick(0, 0)}></Cell>
        </tr>
      </table>
    </div>
  );
}

export default Gameboard;

