import { useState, useEffect } from 'react';
import Cell from './Cell';

function getRandomValue() {
  let max = 36;
  let min = 1;
  return Math.floor(Math.random() * (max - min) + min);
}

const Gameboard = () => {

  const rows = 6;
  const cols = 6;
  let driver = [];
  driverInit();

  function driverInit(){
    for(let i = 0; i < rows; i++){
      driver[i] = [];
        for(let j = 0; j < cols; j++){
          driver[i][j] = {isClicked: false, value: getRandomValue()};
        }
    }
  }

  function getDriver(){
    return driver;
  }

  let gameboardRows = [];
  function updateBoard(){
    gameboardRows = [];
        // create an array of rows with cells in them
        for (let i = 0; i < rows; i++) {
          const rowCells = [];
          for (let j = 0; j < cols; j++) {
            console.log(getDriver()[i][j].isClicked);
            rowCells.push(
              <Cell
                key={`${i}-${j}`}
                value={getDriver()[i][j].value}
                isClicked={getDriver()[i][j].isClicked}
                onClick={() => handleClick(i, j)}
              />
            );
          }
          gameboardRows.push(<tr key={i}>{rowCells}</tr>);
        }
  }

  //updating values whenever user clicks on a cell
  const handleClick = (row, col) => {
    driver[row][col].isClicked = !driver[row][col].isClicked;
    updateBoard();
  };

  updateBoard();

  return (
    <div className="gameboard">
      <table>
        <tbody>
          {gameboardRows}
        </tbody>
      </table>
    </div>
  );
}

export default Gameboard;

