import React, { useState, useEffect } from "react";

function getRandomValue() {
    let max = 36;
    let min = 1;
    return Math.floor(Math.random() * (max - min) + min);
}

const Cell = (props) => {
    const [clicked, setClicked] = useState(false);
    const [bgColor, setBgColor] = useState("#ffffff2e");
    const [value, setValue] = useState(getRandomValue());

    useEffect(() => {
        if (clicked) {
            setBgColor("#ffffff6d");
        } else {
            setBgColor("#ffffff2e");
        }
    }, [clicked]);

    const handleClick = () => {
        setClicked(!clicked);
    };

    return (
        <th onClick={handleClick} style={{ backgroundColor: bgColor }}>
            {value}
        </th>
    );
};

export default Cell;
