import './App.css';
import Gameboard from './Gameboard';
function App() {

  let currentLevel = 1;

  const handleClick = () => {
    if(document.getElementById("endbtn").textContent == "Next Level"){
      currentLevel++;
    }
    else{
      window.location.reload();
    }
  };

  return (
    <div className="App">
      <section className="page">
        <header>
        <div className="content">
          <div className="title">
            <h1>GLOWBIT</h1>
          </div>
          <Gameboard level={currentLevel}></Gameboard>
          <div id="aftergame" className="gameover">
            <h1 id="gameover"></h1>
            <h2 id="winstatus"></h2>
            <div className="runtime">
            <p id="time"></p>
          </div>
          <div className="buttons">
            <button onClick={handleClick} id="endbtn"></button>
            {/* <button>Next Level</button> */}
          </div>
          </div>
        </div>
        </header>
      </section>
    </div>
  );
}

export default App;
