import './App.css';
import Gameboard from './Gameboard';
import React, { useState } from "react";

function App() {

  const [level, setLevel] = useState(1);

  const resetGame = () => {
    setLevel(level + 1);
  };

  return (
    <div className="App">
      <section className="page">
        <header>
        <div className="content">
          <div className="title">
            <h1>GLOWBIT</h1>
          </div>
          <Gameboard level={level} resetGame={resetGame}></Gameboard>
          <div id="aftergame" className="gameover">
            <h1 id="gameover"></h1>
            <h2 id="winstatus"></h2>
            <div className="runtime">
            <p id="time"></p>
          </div>
          <div className="buttons">
            <button id="endbtn"></button>
            {/* <button>Next Level</button> */}
          </div>
          </div>
        </div>
        </header>
      </section>
    </div>
  );
}

export default App;
