import { useState } from 'react';
import Cell from './Cell';

//Initializing a gameboard 2D array to store values
const Gameboard = () => {
  const [board, setBoard] = useState([
    [false, false, false, false, false, false],
    [false, false, false, false, false, false],
    [false, false, false, false, false, false],
    [false, false, false, false, false, false],
    [false, false, false, false, false, false],
    [false, false, false, false, false, false],
  ]);

  //Function to handle when the user clicks a cell
  const handleClick = (row, col) => {
    //setting updated board equal to the current board
    const updatedBoard = [...board];
    //switching the chosen value
    updatedBoard[row][col] = !board[row][col];
    //will switch out board to the current one
    setBoard(updatedBoard);
  };

  //Finding the most efficient path using dynamic programming
  let numRows = board.length;
  let numCols = board[0].length;

  //creating and initializing 2D memoization table
  const memoizationTable = [];
  for (let i = 0; i < numRows; i++) {
    memoizationTable.push(new Array(numCols).fill(0));
  }

  return (  
    <div className="gameboard">
      <table>
        <tbody>
          {board.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {row.map((isClicked, colIndex) => (
                <Cell value="0" key={colIndex} isClicked={isClicked} onClick={() => handleClick(rowIndex, colIndex)} />
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Gameboard;
