import React, { useState } from "react";

let clicked = false;
let bgColor = "#ffffff2e";

const Cell = (input) => {

    let max = 36;
    let min = 1;
    let value = Math.floor(Math.random() * (max - min) + min);

    const handleClick = () => {
        clicked = !clicked;
        if(clicked == true){
            bgColor = "#ffffff6d";
        }
        else{
            bgColor = "#ffffff2e";
        }
    };

    return (
        <th onClick={handleClick} style={{
            backgroundColor: "#000000"
        }}>{value}</th>
     );
}

export default Cell;
