import { useState, useEffect } from 'react';
import Cell from './Cell';

function getRandomValue() {
  let max = 36;
  let min = 1;
  return Math.floor(Math.random() * (max - min) + min);
}


const Gameboard = () => {

  //initializing driver table
  const [board, setBoard] = useState([
    [null, null, null, null, null, null],
    [null, null, null, null, null, null],
    [null, null, null, null, null, null],
    [null, null, null, null, null, null],
    [null, null, null, null, null, null],
    [null, null, null, null, null, null]
  ]);
  const [values, setValues] = useState([]);
  useEffect(() => {
    const newBoard = board.map(row =>
      row.map(() => ({ isClicked: false, value: getRandomValue() }))
    );
    setBoard(newBoard);
    setValues(newBoard.flat().map(pair => pair.value));
  }, []);

  //updating values whenever user clicks on a cell
  const handleClick = (row, col) => {
    const updatedBoard = [...board];
    updatedBoard[row][col].isClicked = !board[row][col].isClicked;
    setBoard(updatedBoard);
  };

  return (  
    <div className="gameboard">
      <table>
        <tbody>
          {board.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {row.map((cell, colIndex) => (
                <Cell
                  key={colIndex}
                  value={cell?.value}
                  isClicked={cell?.isClicked}
                  onClick={() => handleClick(rowIndex, colIndex)}
                />
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Gameboard;
