import './App.css';
import Gameboard from './Gameboard';
import { useState } from 'react';

function App() {
  const [currentLevel, setCurrentLevel] = useState(1);
  const [isGameOver, setIsGameOver] = useState(false);
  const [isGameWon, setIsGameWon] = useState(false);

  const handleButtonClick = () => {
    if (isGameWon) {
      setCurrentLevel(currentLevel + 1);
      setIsGameWon(false);
      setIsGameOver(false);
    } else {
      window.location.reload();
    }
  };

  return (
    <div className="App">
      <section className="page">
        <header>
          <div className="content">
            <div className="title">
              <h1>GLOWBIT</h1>
            </div>
            <Gameboard level={currentLevel} onGameOver={() => setIsGameOver(true)} onGameWin={() => setIsGameWon(true)} />
            {isGameOver && (
              <div id="aftergame" className="gameover">
                <h1 id="gameover">Game Over</h1>
                <h2 id="winstatus">{isGameWon ? 'You Won!' : 'You Lost'}</h2>
                <div className="runtime">
                  <p id="time"></p>
                </div>
                <div className="buttons">
                  <button onClick={handleButtonClick} id="endbtn">{isGameWon ? 'Next Level' : 'Play Again'}</button>
                </div>
              </div>
            )}
          </div>
        </header>
      </section>
    </div>
  );
}

export default App;



