import React, { useState, useEffect } from "react";

function getRandomValue() {
  let max = 36;
  let min = 1;
  return Math.floor(Math.random() * (max - min) + min);
}

const Cell = (props) => {
  const [bgColor, setBgColor] = useState("#ffffff2e");
  const [value, setValue] = useState(getRandomValue());

  useEffect(() => {
    if (props.isClicked) {
      setBgColor("#ffffff6d");
    } else {
      setBgColor("#ffffff2e");
    }
  }, [props.isClicked]);

  const handleClick = () => {
    props.onClick();
  };

  return (
    <th style={{ backgroundColor: bgColor }} onClick={handleClick}>
      {value}
    </th>
  );
};

export default Cell;
